      <nuxt-link
        v-for="(menu, index) in menus"
        :key="index"
        :to="menu.path"
        @click.native="handleClickMenuItem()"
        class="flex gap-3 items-center md:justify-center lg:justify-center xl:justify-start cursor-pointer group h-9 px-3 rounded-xl hover:bg-gray-400/10 transition-colors"
